import React, { useState } from 'react';

function App() {
  const [isRed, setIsRed] = useState(true);

  const toggleColor = () => {
    setIsRed(prevIsRed => !prevIsRed);
  };

  return (
    <div className="App">
      <div
        className="square"
        style={{ backgroundColor: isRed ? 'red' : 'blue' }}
        onClick={toggleColor}
      ></div>
      <div
        className="square"
        style={{ backgroundColor: isRed ? 'blue' : 'red' }}
        onClick={toggleColor}
      ></div>
    </div>
  );
}

export default App;
